// src/components/TourList.js
import React from 'react';
import { Link } from 'react-router-dom';

const TourList = ({ tours = [], currentPage = 1, totalPages = 1, onPageChange = () => {} }) => {
  return (
    <div className="tour-list">
      {tours.length === 0 ? (
        <p>Không tìm thấy tour nào.</p>
      ) : (
        <div className="tour-grid">
          {tours.map((tour) => (
            <div className="tour-card" key={tour.tourId}>
              <img
                src={tour.images[0]}
                alt={tour.title}
                style={{ width: '100%', height: '200px', objectFit: 'cover' }}
              />
              <div className="tour-card-body">
                <h4>
                  <Link to={`/tours/${tour.tourId}`}>{tour.title}</Link>
                </h4>
                <p><strong>Thời gian:</strong> {tour.time}</p>
                <p><strong>Giá:</strong> {tour.priceAdult.toLocaleString()} VND</p>
                <p><strong>Đánh giá:</strong> {tour.rating}⭐</p>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="pagination">
          <button
            disabled={currentPage === 1}
            onClick={() => onPageChange(currentPage - 1)}
          >
            Trang trước
          </button>
          <span>
            {currentPage} / {totalPages}
          </span>
          <button
            disabled={currentPage === totalPages}
            onClick={() => onPageChange(currentPage + 1)}
          >
            Trang sau
          </button>
        </div>
      )}
    </div>
  );
};

export default TourList;
