import React, { useState, useEffect } from "react";
import axios from "axios";
import BannerHome from "../components/BannerHome";
import ManagerCampingList from "../components/ManagerCampingList";
import SearchBar from "../components/SearchBar";
const ManagerCamping = () => {
  const itemsPerPage = 6;
  const [currentPage, setCurrentPage] = useState(1);
  const [filteredTours, setFilteredTours] = useState([]);
  const [allTours, setAllTours] = useState([]);

  const [searchParams, setSearchParams] = useState({
    filter: "all", // mặc định lấy tất cả
  });

  // 👉 Hardcode ownerId để test
  const ownerId = "a7ea138e-3ba5-42a3-9686-2c7f9a9d57f4";

  const handleSearch = (params) => {
    setSearchParams(params);
    setCurrentPage(1);
  };

  // 👉 Gọi API lấy danh sách camping
  useEffect(() => {
    const fetchTours = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/v1/camping");
        setAllTours(res.data);
      } catch (err) {
        console.error("Lỗi khi gọi API:", err);
      }
    };

    fetchTours();
  }, []);

  // 👉 Lọc tours theo ownerId + filter
  useEffect(() => {
    let result = allTours;

    // chỉ lấy camp của owner
    result = result.filter((tour) => tour.ownerId === ownerId);

    // lọc theo filter
    if (searchParams.filter === "active") {
      result = result.filter((tour) => tour.active === true);
    } else if (searchParams.filter === "in-progress") {
      result = result.filter((tour) => tour.active === false);
    }

    setFilteredTours(result);
  }, [searchParams, allTours, ownerId]);

  const indexOfLastTour = currentPage * itemsPerPage;
  const indexOfFirstTour = indexOfLastTour - itemsPerPage;
  const currentTours = filteredTours.slice(indexOfFirstTour, indexOfLastTour);
  const totalPages = Math.ceil(filteredTours.length / itemsPerPage);

  return (
    <>
      <BannerHome onSearch={handleSearch}/>
      <SearchBar/>
      <div className="tour-grid-wrap">
        <div className="row" id="tours-container">
          <ManagerCampingList
            tours={currentTours}
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </div>
      </div>
    </>
  );
};

export default ManagerCamping;
