import React, { useState, useEffect } from 'react';
import TourList from '../components/TourList';
import { tours as allTours } from '../data/mockData';
import BannerHome from '../components/BannerHome';

const TourScreen = () => {
  const itemsPerPage = 6;
  const [currentPage, setCurrentPage] = useState(1);
  const [filteredTours, setFilteredTours] = useState(allTours);

  const [searchParams, setSearchParams] = useState({
    destination: '',
    start_date: '',
    end_date: '',
  });

  const handleSearch = (params) => {
    setSearchParams(params);
    setCurrentPage(1); // reset về page 1 khi tìm kiếm
  };

  useEffect(() => {
    let result = allTours;

    // Lọc theo điểm đến
    if (searchParams.destination) {
      result = result.filter((tour) =>
        tour.destination.toLowerCase().includes(searchParams.destination.toLowerCase())
      );
    }

    // Bạn có thể mở rộng lọc theo ngày nếu dữ liệu tour có ngày (ví dụ tour.startDate)

    setFilteredTours(result);
  }, [searchParams]);

  const indexOfLastTour = currentPage * itemsPerPage;
  const indexOfFirstTour = indexOfLastTour - itemsPerPage;
  const currentTours = filteredTours.slice(indexOfFirstTour, indexOfLastTour);
  const totalPages = Math.ceil(filteredTours.length / itemsPerPage);

  return (
    <>
      <BannerHome onSearch={handleSearch} />

      <div className="tour-grid-wrap">
        <div className="row" id="tours-container">
          <TourList
            tours={currentTours}
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </div>
      </div>
    </>
  );
};

export default TourScreen;
