// src/screens/TourDetailScreen.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { tours } from '../data/mockData';
import "./TourDetailScreen.css";

const TourDetailScreen = () => {
  const { id } = useParams();
  const tour = tours.find((t) => t.tourId === parseInt(id));

  const [comments, setComments] = useState(tour.comments || []);
  const [newComment, setNewComment] = useState('');

  if (!tour) return <p>Tour không tồn tại.</p>;

  const handleAddComment = () => {
    if (!newComment.trim()) return;
    const newEntry = {
      id: comments.length + 1,
      user: 'Khách',
      text: newComment,
    };
    setComments([...comments, newEntry]);
    setNewComment('');
  };

  return (
    <div className="tour-detail-container">
      <h2>{tour.title}</h2>
      <img src={tour.images[0]} alt={tour.title} style={{ width: '100%', maxHeight: '400px', objectFit: 'cover' }} />
      <p><strong>Thời gian:</strong> {tour.time}</p>
      <p><strong>Giá người lớn:</strong> {tour.priceAdult.toLocaleString()} VND</p>
      <p><strong>Mô tả:</strong> {tour.description}</p>

      <h3>Lịch trình</h3>
      <ul>
        {tour.schedule.map((day, idx) => (
          <li key={idx}>{day}</li>
        ))}
      </ul>

      <h3>Bình luận</h3>
      <div className="comments-section">
        {comments.length === 0 ? (
          <p>Chưa có bình luận nào.</p>
        ) : (
          <ul>
            {comments.map((c) => (
              <li key={c.id}><strong>{c.user}:</strong> {c.text}</li>
            ))}
          </ul>
        )}
      </div>

      <div className="comment-form">
        <textarea
          placeholder="Viết bình luận..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        ></textarea>
        <button onClick={handleAddComment}>Gửi</button>
      </div>
    </div>
  );
};

export default TourDetailScreen;
