import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import Banner from '../components/Banner';
import Footer from '../components/Footer';
import Newsletter from '../components/Newsletter';
import FilterSidebar from '../components/FilterSidebar';
import TourCard from '../components/TourCard';
import { fetchTours, fetchPopularTours } from '../api/tourApi';

export default function TourGridPage() {
  const [tours, setTours] = useState([]);
  const [popularTours, setPopularTours] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadTours = async () => {
      setLoading(true);
      const data = await fetchTours();
      setTours(data);
      const popular = await fetchPopularTours();
      setPopularTours(popular);
      setLoading(false);
    };
    loadTours();
  }, []);

  return (
    <>
      <Header />
      <Banner />
      <section className="tour-grid-page py-100 rel z-1">
        <div className="container">
          <div className="row">
            <div className="col-lg-3 col-md-6 col-sm-10 rmb-75">
              <FilterSidebar popularTours={popularTours} />
            </div>

            <div className="col-lg-9">
              <div className="shop-shorter rel z-3 mb-20">
                <div className="sort-text mb-15 me-4 me-xl-auto">Tours tìm thấy</div>
                <div className="sort-text mb-15 me-4">Sắp xếp theo</div>
                <select id="sorting_tours">
                  <option value="default">Sắp xếp theo</option>
                  <option value="new">Mới nhất</option>
                  <option value="old">Cũ nhất</option>
                  <option value="hight-to-low">Cao đến thấp</option>
                  <option value="low-to-high">Thấp đến cao</option>
                </select>
              </div>

              <div className="tour-grid-wrap">
                {loading ? (
                  <div className="loader">Loading...</div>
                ) : (
                  <div className="row" id="tours-container">
                    {tours.map((tour) => (
                      <div className="col-md-6 col-xl-4 mb-4" key={tour.id}>
                        <TourCard tour={tour} />
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </section>
      <Newsletter />
      <Footer />
    </>
  );
}
