import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { tours } from '../data/mockData';
import "./TourDetailScreen.css";

const normalize = (rawComments = []) =>
  rawComments.map(c => ({
    id: c.id ?? Math.floor(Date.now() + Math.random() * 10000),
    user: c.user ?? 'Kh√°ch',
    text: c.text ?? '',
    likes: Number(c.likes) || 0,
    dislikes: Number(c.dislikes) || 0,
    replies: (c.replies || []).map(r => ({
      id: r.id ?? Math.floor(Date.now() + Math.random() * 10000),
      user: r.user ?? 'Kh√°ch',
      text: r.text ?? '',
      likes: Number(r.likes) || 0,
      dislikes: Number(r.dislikes) || 0
    }))
  }));

const TourDetailScreen = () => {
  const { id } = useParams();
  const tour = tours.find((t) => t.tourId === parseInt(id));
  const [comments, setComments] = useState(() => normalize(tour?.comments || []));
  const [newComment, setNewComment] = useState('');

  if (!tour) return <p>Tour kh√¥ng t·ªìn t·∫°i.</p>;

  const handleAddComment = () => {
    if (!newComment.trim()) return;
    const newEntry = {
      id: Math.floor(Date.now() + Math.random() * 10000),
      user: 'Kh√°ch',
      text: newComment,
      likes: 0,
      dislikes: 0,
      replies: []
    };
    setComments(prev => [...prev, newEntry]);
    setNewComment('');
  };

  // C·∫≠p nh·∫≠t vote cho c·∫£ comment v√† reply, an to√†n v·ªõi undefined
  const handleVote = (targetId, type) => {
    setComments(prev =>
      prev.map(comment => {
        // n·∫øu target l√† comment ch√≠nh
        if (comment.id === targetId) {
          return {
            ...comment,
            likes: type === 'like' ? (Number(comment.likes) || 0) + 1 : comment.likes,
            dislikes: type === 'dislike' ? (Number(comment.dislikes) || 0) + 1 : comment.dislikes
          };
        }

        // n·∫øu target l√† 1 reply trong comment n√†y
        if (comment.replies?.some(r => r.id === targetId)) {
          return {
            ...comment,
            replies: comment.replies.map(r =>
              r.id === targetId
                ? {
                    ...r,
                    likes: type === 'like' ? (Number(r.likes) || 0) + 1 : r.likes,
                    dislikes: type === 'dislike' ? (Number(r.dislikes) || 0) + 1 : r.dislikes
                  }
                : r
            )
          };
        }

        return comment;
      })
    );
  };

  const handleReply = (commentId, replyText) => {
    if (!replyText.trim()) return;
    const newReply = {
      id: Math.floor(Date.now() + Math.random() * 10000),
      user: 'Kh√°ch',
      text: replyText,
      likes: 0,
      dislikes: 0
    };
    setComments(prev =>
      prev.map(c =>
        c.id === commentId ? { ...c, replies: [...(c.replies || []), newReply] } : c
      )
    );
  };

  return (
    <div className="tour-detail">
      <h2>{tour.title}</h2>
      <img
        src={tour.images[0]}
        alt={tour.title}
        style={{ width: '100%', maxHeight: '400px', objectFit: 'cover' }}
      />
      <p><strong>Th·ªùi gian:</strong> {tour.time}</p>
      <p><strong>Gi√° ng∆∞·ªùi l·ªõn:</strong> {tour.priceAdult.toLocaleString()} VND</p>
      <p><strong>M√¥ t·∫£:</strong> {tour.description}</p>

      <h4>B√¨nh lu·∫≠n</h4>
      <div className="comments-section">
        {comments.length === 0 ? (
          <p>Ch∆∞a c√≥ b√¨nh lu·∫≠n n√†o.</p>
        ) : (
          <ul>
            {comments.map(c => (
              <CommentItem
                key={c.id}
                comment={c}
                onVote={handleVote}
                onReply={handleReply}
              />
            ))}
          </ul>
        )}
      </div>

      <div className="comment-form">
        <textarea
          placeholder="Vi·∫øt b√¨nh lu·∫≠n..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        />
        <button onClick={handleAddComment}>G·ª≠i</button>
      </div>
    </div>
  );
};

const CommentItem = ({ comment, onVote, onReply }) => {
  const [replyText, setReplyText] = useState('');
  const [showReplyBox, setShowReplyBox] = useState(false);

  const handleSendReply = () => {
    onReply(comment.id, replyText);
    setReplyText('');
    setShowReplyBox(false);
  };

  return (
    <li className="comment-item">
      <p><strong>{comment.user}:</strong> {comment.text}</p>
      <div className="comment-actions">
        <button onClick={() => onVote(comment.id, 'like')}>üëç {comment.likes ?? 0}</button>
        <button onClick={() => onVote(comment.id, 'dislike')}>üëé {comment.dislikes ?? 0}</button>
        <button onClick={() => setShowReplyBox(!showReplyBox)}>
          {showReplyBox ? "H·ªßy" : "Tr·∫£ l·ªùi"}
        </button>
      </div>

      {showReplyBox && (
        <div className="reply-form">
          <textarea
            placeholder="Vi·∫øt ph·∫£n h·ªìi..."
            value={replyText}
            onChange={(e) => setReplyText(e.target.value)}
          />
          <button onClick={handleSendReply}>G·ª≠i</button>
        </div>
      )}

      {comment.replies?.length > 0 && (
        <ul className="reply-list">
          {comment.replies.map(r => (
            <li key={r.id} className="reply-item">
              <p><strong>{r.user}:</strong> {r.text}</p>
              <div className="comment-actions">
                <button onClick={() => onVote(r.id, 'like')}>üëç {r.likes ?? 0}</button>
                <button onClick={() => onVote(r.id, 'dislike')}>üëé {r.dislikes ?? 0}</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </li>
  );
};

export default TourDetailScreen;
