// src/screens/tour.js
import React, { useState } from 'react';
import TourList from '../components/TourList';
import BannerHome from '../components/BannerHome';
import { tours as allTours } from '../data/mockData';

const Tours = () => {
  const itemsPerPage = 6;
  const [currentPage, setCurrentPage] = useState(1);

  const totalPages = Math.ceil(allTours.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentTours = allTours.slice(startIndex, startIndex + itemsPerPage);

  return (
    
    <div className="container">
      <BannerHome onSearch={handleSearch} />
      <h2>Danh s√°ch tour</h2>
      <div className="row">
        <TourList
          tours={currentTours}
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={setCurrentPage}
        />
      </div>
    </div>
  );
};

export default Tours;
