import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { tours } from '../data/mockData';
import "./TourDetailScreen.css";

const TourDetailScreen = () => {
  const { id } = useParams();
  const tour = tours.find((t) => t.tourId === parseInt(id));

  const [comments, setComments] = useState(tour?.comments || []);
  const [newComment, setNewComment] = useState('');

  if (!tour) return <p>Tour kh√¥ng t·ªìn t·∫°i.</p>;

  // üîπ Th√™m b√¨nh lu·∫≠n m·ªõi
  const handleAddComment = () => {
    if (!newComment.trim()) return;
    const newEntry = {
      id: Date.now(),
      user: 'Kh√°ch',
      text: newComment,
      likes: 0,
      dislikes: 0,
      replies: []
    };
    setComments([...comments, newEntry]);
    setNewComment('');
  };

  // üîπ X·ª≠ l√Ω Like/Dislike
  const handleVote = (commentId, type) => {
    setComments(comments.map(c => {
      if (c.id === commentId) {
        return {
          ...c,
          likes: type === 'like' ? c.likes + 1 : c.likes,
          dislikes: type === 'dislike' ? c.dislikes + 1 : c.dislikes
        };
      }
      return c;
    }));
  };

  // üîπ Tr·∫£ l·ªùi b√¨nh lu·∫≠n
  const handleReply = (commentId, replyText) => {
    if (!replyText.trim()) return;
    setComments(comments.map(c => {
      if (c.id === commentId) {
        return {
          ...c,
          replies: [
            ...c.replies,
            {
              id: Date.now(),
              user: "Kh√°ch",
              text: replyText,
              likes: 0,
              dislikes: 0
            }
          ]
        };
      }
      return c;
    }));
  };

  return (
    <div className="tour-detail">
      <h2>{tour.title}</h2>
      <img 
        src={tour.images[0]} 
        alt={tour.title} 
        style={{ width: '100%', maxHeight: '400px', objectFit: 'cover' }} 
      />
      <p><strong>Th·ªùi gian:</strong> {tour.time}</p>
      <p><strong>Gi√° ng∆∞·ªùi l·ªõn:</strong> {tour.priceAdult.toLocaleString()} VND</p>
      <p><strong>M√¥ t·∫£:</strong> {tour.description}</p>

      <h4>L·ªãch tr√¨nh</h4>
      <ul>
        {tour.schedule.map((day, idx) => (
          <li key={idx}>{day}</li>
        ))}
      </ul>

      <h4>B√¨nh lu·∫≠n</h4>
      <div className="comments-section">
        {comments.length === 0 ? (
          <p>Ch∆∞a c√≥ b√¨nh lu·∫≠n n√†o.</p>
        ) : (
          <ul>
            {comments.map((c) => (
              <CommentItem
                key={c.id}
                comment={c}
                onVote={handleVote}
                onReply={handleReply}
              />
            ))}
          </ul>
        )}
      </div>

      <div className="comment-form">
        <textarea
          placeholder="Vi·∫øt b√¨nh lu·∫≠n..."
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
        ></textarea>
        <button onClick={handleAddComment}>G·ª≠i</button>
      </div>
    </div>
  );
};

// üîπ Component con hi·ªÉn th·ªã 1 b√¨nh lu·∫≠n
const CommentItem = ({ comment, onVote, onReply }) => {
  const [replyText, setReplyText] = useState('');
  const [showReplyBox, setShowReplyBox] = useState(false);

  const handleSendReply = () => {
    onReply(comment.id, replyText);
    setReplyText('');
    setShowReplyBox(false);
  };

  return (
    <li className="comment-item">
      <p><strong>{comment.user}:</strong> {comment.text}</p>
      <div className="comment-actions">
        <button onClick={() => onVote(comment.id, 'like')}>üëç {comment.likes}</button>
        <button onClick={() => onVote(comment.id, 'dislike')}>üëé {comment.dislikes}</button>
        <button onClick={() => setShowReplyBox(!showReplyBox)}>
          {showReplyBox ? "H·ªßy" : "Tr·∫£ l·ªùi"}
        </button>
      </div>

      {showReplyBox && (
        <div className="reply-form">
          <textarea
            placeholder="Vi·∫øt ph·∫£n h·ªìi..."
            value={replyText}
            onChange={(e) => setReplyText(e.target.value)}
          ></textarea>
          <button onClick={handleSendReply}>G·ª≠i</button>
        </div>
      )}

      {comment.replies?.length > 0 && (
        <ul className="reply-list">
          {comment.replies.map(r => (
            <li key={r.id} className="reply-item">
              <p><strong>{r.user}:</strong> {r.text}</p>
              <div className="comment-actions">
                <button onClick={() => onVote(r.id, 'like')}>üëç {r.likes}</button>
                <button onClick={() => onVote(r.id, 'dislike')}>üëé {r.dislikes}</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </li>
  );
};

export default TourDetailScreen;
