import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function BannerHome({ onSearch }) {
  const [activeFilter, setActiveFilter] = useState("all");
  const navigate = useNavigate();

  useEffect(() => {
    const loadScript = (src) =>
      new Promise((resolve, reject) => {
        const script = document.createElement("script");
        script.src = src;
        script.async = false;
        script.onload = resolve;
        script.onerror = reject;
        document.body.appendChild(script);
      });

    const loadScripts = async () => {
      try {
        await loadScript("/assets/js/aos.js");

        if (window.AOS) {
          window.AOS.init();
        }
      } catch (error) {
        console.error("Failed to load script:", error);
      }
    };

    loadScripts();
  }, []);

  const handleFilter = (filterType) => {
    setActiveFilter(filterType);

    // Nếu đang không ở trang managercamping thì chuyển trang
    navigate(`/managercamping?filter=${filterType}`);

    // Nếu đang ở trang managercamping thì gọi luôn onSearch
    if (onSearch) {
      onSearch({ filter: filterType });
    }
  };

  const getButtonClass = (filterType) =>
    `theme-btn px-6 py-2 ${
      activeFilter === filterType ? "active-btn" : ""
    }`;

  return (
    <section className="hero-area bgc-black pt-200 rpt-120 rel z-2">
      <div className="container-fluid">
        <h1 className="hero-title">CAMPVERSE</h1>
        <div
          className="main-hero-image bgs-cover"
          style={{ backgroundImage: `url(/assets/images/hero/hero.jpg)` }}
        ></div>
      </div>

      {/* Search bằng 3 nút bấm */}
      <div className="container container-1400">
        <div className="search-filter-inner flex justify-center gap-4 mt-6">
          <button
            type="button"
            className={getButtonClass("all")}
            onClick={() => handleFilter("all")}
          >
            <span>Tất cả Camping</span>
          </button>

          <button
            type="button"
            className={getButtonClass("active")}
            onClick={() => handleFilter("active")}
          >
            <span>Camping đã Active</span>
          </button>

          <button
            type="button"
            className={getButtonClass("in-progress")}
            onClick={() => handleFilter("in-progress")}
          >
            <span>Camping đang xem xét</span>
          </button>
        </div>
      </div>
    </section>
  );
}
